import { useState } from 'react'

const Button = ({ handleClick, text }) => (
  <button onClick={handleClick}>
    {text}
  </button>
)

const Ka = () => {
  let ka
}

const App = () => {
  // muuttujat
  const [good, setGood] = useState(0)
  const [neutral, setNeutral] = useState(0)
  const [bad, setBad] = useState(0)
  const [allClicks, setAll] = useState(0) /*laskee kaikkien annettujen äänien kappale määrän*/
  const [allClicksYht, setAllYht] = useState(1) /*laskee kaikkien annettujen äänien marvosanat yhteen*/
  const [ka, setKa] = useState(0) /*laskee äänten keskiarvon*/

  const handleGoodClick = () => {
    setGood(good + 1)
    setAll(allClicks + 1)
    console.log(allClicks)
    setAllYht(allClicksYht + 1)
    console.log(allClicksYht)
  }

  const handleNeutralClick = () => {
    setNeutral(neutral + 1)
    setAll(allClicks + 1)
    console.log(allClicks)
    setAllYht(allClicksYht)
    console.log(allClicksYht)
  }

  const handleBadClick = () => {
    setBad(bad + 1)
    setAll(allClicks + 1)
    console.log(allClicks)
    setAllYht(allClicksYht - 1)
    console.log(allClicksYht)
  }

  return (
    <div>
      <h1>Anna palautetta</h1>
      <Button handleClick={handleGoodClick} text='Huippu!' />
      <Button handleClick={handleNeutralClick} text='Neutraali' />
      <Button handleClick={handleBadClick} text='Huono!' />
      <h2>Statistiikka</h2>
      <p>Huippu: {good}</p>  
      <p>Neutraali: {neutral}</p>  
      <p>Huono: {bad}</p> 
      <p>Ääniä yhteensä: {allClicks} </p> 
      <p>Äänien keskiarvo: {allClicksYht}</p>
      <p>Äänistä positiivisia:  %</p>
    </div>
  )
}


export default App
