    function HandleClick(maa) {
      setNaytettavaMaa(maa)
      isClicked = true ///miten????
      console.log(isClicked)
    }
    return (
      <div>
        {isClicked && (
          <div>
            <ul>
              <Maa key={naytettavaMaa.name} maa={naytettavaMaa} />
            </ul>
          </div>    
