/* Iida Peltonen 2022 */

import React, { useState } from 'react'
import Maa from './Maa'

const Haku = ({ maat }) => {
  const [wordEntered, setWordEntered] = useState('')
  const [filteredData, setFilteredData] = useState([maat])
  const [tulos, setTulos] = useState('')

  function handleHaku (e) {
    const hakusana = e.target.value
    setWordEntered(hakusana)
    const newFilter = maat.filter(value => {
      return value.name.official.toLowerCase().includes(hakusana.toLowerCase())
    })
    setFilteredData(newFilter)
    setTulos(newFilter.length)
    /*  console.log('filtteri', newFilter.length)
        console.log('tulos', tulos) */
  }
  console.log('tulos jälkeen', tulos)
  return (
    <div>
      Etsi maa <input onChange={handleHaku} value={wordEntered} />
      {tulos > 10 && (
            <div>
                <p>
                    Liikaa hakutuloksia, tarkenna hakua
                </p>
            </div>
          //tulostaa tekstin "tarkenna hakea"
        )} 
        {tulos < 10 && tulos >0

        }
      {wordEntered.length !== 0 && (
        <div>
          <ul>
            {filteredData.map(maa => (
              <Maa key={maa.name.official} maa={maa} koodi={maa.numericCode} />
            ))}
          </ul>
        </div>
      )}
      {wordEntered.length === 0 && (
        <div>
          <ul>
            {maat.map(maa => (
              <Maa key={maa.name.official} maa={maa} koodi={maa.numericCode} />
            ))}
          </ul>
        </div>
      )}
    </div>
  )
  console.log(tulos)
}

export default Haku
