/* Iida Peltonen 2022 */

import { useState, useEffect } from 'react';
import axios from 'axios';

const URL = 'https://restcountries.com/v3.1/all';

function App() {
  const [maat, setMaat] = useState([]);

  useEffect(getData, [])

  function getData() {
    axios.get(URL)
      .then((response) => {
        //console.log(response.data)
        setMaat(response.data);
      }).catch(error => {
        alert(error);
      });
  }

  return (
    <div>
      <h1></h1>Maiden tiedot
      <h3>Maat</h3>
      <ol>
        {maat.map(maa => (
          <li key={maa.fifa}>{maa.flag} {maa.name.official}</li>
        ))}
      </ol>
    </div>
  );
}

