/* Iida Peltonen 2022 */

import { useState, useEffect } from 'react'
import Filter from './components/Filter'
import UudenLisays from './components/UudenLisays'
import personService from './services/persons'

const App = props => {
  const [persons, setPersons] = useState(props.persons) //kaikki tyypit

  useEffect(() => {
    personService
      .getAll()
      .then(initialPersons => {
        setPersons(initialPersons)
      })
  }, [])

  const Poista = ( person ) => {
    let vastaus = false
    vastaus = window.confirm(`Poistetaanko henkilö ${person.name}?`)
    if (vastaus) {
      personService.remove(person.id)
      alert(`${person.name} poistettu`)
    }
  }

  return (
    <div>
      <h1>Puhelinluettelo</h1>
      <Filter persons={persons} />
      <button onClick={() => Poista(person)}>Poista</button>
      <h2>Tallenna uusi</h2>
      <UudenLisays persons={persons} setPersons={setPersons} />
    </div>
  )
}

export default App
