import { useState, useEffect } from "react";
import Persons from "./components/Persons";
import Filter from "./components/Filter";
import personService from "./services/persons";

const App = () => {
  const [persons, setPersons] = useState([]);

  const [filteredPersons, setFilteredPersons] = useState([{}]);
  const [newName, setNewName] = useState("");
  const [newNumber, setNewNumber] = useState("");
  const [newFilter, setNewFilter] = useState("");

  const [successMessage, setSuccessMessage] = useState(null);
  useEffect(() => {
    personService.getAll().then((initialPersons) => {
      setPersons(initialPersons);
    });
    setFilteredPersons(persons);
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);
  console.log("render", persons.length, "persons");
  console.log("filteredPersons ", filteredPersons);

  const addName = (event) => {
    event.preventDefault();
    const nameObject = {
      name: newName,
      number: newNumber,
    };
    let id;
    let namesMatch = false;
    let name;
    persons.forEach((item, index) => {
      if (item.name.toLowerCase() === newName.toLowerCase()) {
        namesMatch = true;
        id = item.id;
      }
    });

    if (namesMatch) {
      let result = window.confirm(
        `${newName} is already added to phonebook, replace the old number with a new one?`
      );
      if (!result) {
        setNewName("");
        setNewNumber("");
      } else {
        personService
          .update(id, nameObject)
          .then((returnedPerson) => {
            setPersons(persons.concat(returnedPerson));
            setNewName("");
            setSuccessMessage(`Modified ${returnedPerson.name}`);
            console.log("update: ", newName);

            setTimeout(() => {
              setSuccessMessage(null);
              window.location.reload(false);
            }, 5000);
          })
          .catch((error) => {
            console.log("error: ", name);

            setSuccessMessage(
              `Information of ${newName} has already been removed from server`
            );
            setTimeout(() => {
              setSuccessMessage(null);
              window.location.reload(false);
            }, 5000);
          });
      }
    } else {
      personService
        .create(nameObject)
        .then((returnedPerson) => {
          setPersons(persons.concat(returnedPerson));
          setNewName("");

          setSuccessMessage(`Added ${returnedPerson.name}`);

          setTimeout(() => {
            setSuccessMessage(null);
            window.location.reload(false);
          }, 5000);
        })
        .catch((error) => {
          const err = error.response.data.error;
          setSuccessMessage(`Error: ${err}`);
          console.log(error.response.data);
        });
      setTimeout(() => {
        setSuccessMessage(null);
        window.location.reload(false);
      }, 5000);
    }
  };

  const handleNameChange = (event) => {
    setNewName(event.target.value);
  };

  const handleNumberChange = (event) => {
    setNewNumber(event.target.value);
  };

  const handleFilterChange = (event) => {
    setFilteredPersons(
      persons.filter((person) =>
        person.name.toLowerCase().includes(event.target.value.toLowerCase())
      )
    );
    setNewFilter(event.target.value);
    console.log(event.target.value);
  };
  return (
    <div>
      <h2>Phonebook</h2>
      <div>
        <Filter value={newFilter} onChange={handleFilterChange} />
      </div>
      <h3>add a new</h3>
      <PersonForm
        onSubmit={addName}
        nameValue={newName}
        onNameChange={handleNameChange}
        numberValue={newNumber}
        onNumberChange={handleNumberChange}
      />

      <h3>Numbers</h3>
      <Notification message={successMessage} />

      <div>
        <Persons allPersons={persons} persons={filteredPersons} />
      </div>
    </div>
  );
};

export default App;
