/* Iida Peltonen 2022 */

import { useState } from 'react'
import personService from '../services/persons'

const UudenLisays = ({ persons, setPersons }) => {
  const [newPerson, setNewPerson] = useState('') //nimet
  const [newNumber, setNewNumber] = useState('') //numerot

  //uuden lisäys
  const LisaaUusi = e => {
    e.preventDefault()
    const personObject = {
      name: newPerson,
      number: newNumber,
      //id: persons.length + 1
    }
    let id;
    let samaNimi

    personService
    .create(personObject)
    .then(returnedPerson => {
      setPersons(persons.concat(returnedPerson))
      setNewPerson('')
    })

    //käydän taulukon nimet läpi ja verrataan
    persons.forEach((item, index) => {
      //jos sama nimi löytyy
      if (item.name.toLowerCase() === newPerson.toLowerCase()) {
        let vastaus = false
        vastaus = window.confirm(newPerson + ' löytyy jo luettelosta, päivitetäänkö numero?')
        if (!vastaus) {
          setNewPerson('')
          setNewNumber('')
        }
        else {
          personService.update(item.id)
          alert(newPerson + 'n numero päivitetty')
        }
      }

      //jos samaa nimeä ei löydy
      else {
        setPersons(persons.concat(personObject))
        setNewPerson('')
        setNewNumber('')
      }
    })
  }


  const handlePersonChange = e => {
    setNewPerson(e.target.value)
  }

  const handleNumberChange = e => {
    setNewNumber(e.target.value)
  }

  return (
    <form onSubmit={LisaaUusi}>
      Nimi: <input value={newPerson} onChange={handlePersonChange} />
      <br />
      Numero: <input value={newNumber} onChange={handleNumberChange} />
      <br />
      <button type='submit'>Tallenna</button>
    </form>
  )
}

export default UudenLisays
