/* Iida Peltonen 2022 */
import React, {useState} from 'react'
import Person from './Person'

const Filter = ({ persons }) => {
    const [haetut, setHaetut] = useState([])
    const [wordEntered, setWordEntered] = useState('')
    const [filteredData, setFilteredData] = useState([])

    function handleFilter (e) {
        const hakusana = e.target.value
        setWordEntered(hakusana)
        const newFilter = persons.filter(value => {
            return (
                value.name.toLowerCase().includes(hakusana.toLowerCase()) 
            )
        })

        if (hakusana === "") {
            setFilteredData([])
        }
        else {
            setFilteredData(newFilter)
        }

   
    return (
        <div>
            Hae kaveria <input onChange={handleFilter}  value={wordEntered}/>
            
            <ul>
                {haetut.map(person =>
                <li> {person.name} {person.number}</li>
                    /* <Person key={person.id} person={haetut} /> */
                )}
                </ul>
            </div>
    )
}
}
     
        

export default Filter
