/* Iida Peltonen 2022 */
import React, {useState} from 'react'
import Person from './Person'

const Filter = ({ persons }) => {
    const [haetut, setHaetut] = persons

    function handleFilter (e) {
        const hakusana = e.target.value
            if (hakusana != "") {
                //käydään taulukon nimet läpi ja verrataan
                persons.forEach((item, index) => {
                    let nimi = item.name
                    //jos jo tallennettu nimi sisältään hakusanan
                    if (nimi.includes(hakusana)) {
                        setHaetut(persons.item)
                        //nämä kaikki pitäisi sisällyttää tauluun, joka näytetään
                        console.log('jee')
                    }
                    //jos ei
                    else {
                        console.log('ei hakutuloksia')
                    }
                })
        }
    }
     
 return (
    <div>
         Hae kaveria <input onChange={handleFilter} />
         <ul>
          {persons.map(person =>
            <Person key={haetut.id} person={person} />
          )}
        </ul>
   </div>
 )
}

export default Filter
